import React, { useState } from 'react';
import axios from "axios";

const LoginForm = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    const authObject = { "Project-ID":  "4e8efe3f-24e2-415f-a8e5-0a4b9eb74ff1", "User-Name": username, "User-Secret": password };

    try{
      //username | password => chatengine -> give messages
      await axios.get("https://api.chatengine.io/chats", { headers: authObject });

      //works out -> logged in
      localStorage.setItem("username", username);
      localStorage.setITem("password", password);

      window.location.reload

    } catch (err) {

    }
  }

  return (
    <div className="wrapper">
      <div className="form">
        <h1 className="title">Chat Application</h1>
        <form onSubmit={handleSubmit}>
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} className="input" placeholder="username" />
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} className="input" placeholder="password" />
          <div align="center">
            <button type="submit" className="button">
              <span>Start Chatting</span>
            </button>
          </div>
        </form>
      </div>
    </div>

  )
}

export default LoginForm;
